package test;

public class OddEvenCheck {

	public void oddEven(int n) {
		if(n%2 == 0) {
			System.out.println(n+" is even");
		}else
			System.out.println(n+" is even");
	}
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int n = 34;
		OddEvenCheck obj = new OddEvenCheck();
		obj.oddEven(n);

	}

}
